<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Asignatura =  $_REQUEST["Asignatura"];   $idMalla =  $_REQUEST["idMalla"];   $codigo =  $_REQUEST["codigo"];   $nombre =  $_REQUEST["nombre"];   $nivel =  $_REQUEST["nivel"];   $idDocumentoPea =  $_REQUEST["idDocumentoPea"];   $horasPractica =  $_REQUEST["horasPractica"];   $horasDocente =  $_REQUEST["horasDocente"];   $horasAutonomas =  $_REQUEST["horasAutonomas"];   if($boton == "BORRAR"){      deleteAsignatura($Asignatura);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Asignatura>0){         updateAsignatura($Asignatura,$idMalla,$codigo,$nombre,$nivel,$idDocumentoPea,$horasPractica,$horasDocente,$horasAutonomas);      }else{         createAsignatura($idMalla,$codigo,$nombre,$nivel,$idDocumentoPea,$horasPractica,$horasDocente,$horasAutonomas);      }   }   header("location: http://".HOST."/sae");?>
