<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $AsignaturaSolicitudMatricula =  $_REQUEST["AsignaturaSolicitudMatricula"];   $idSolicitudMatricula =  $_REQUEST["idSolicitudMatricula"];   $idAsignatura =  $_REQUEST["idAsignatura"];   if($boton == "BORRAR"){      deleteAsignaturaSolicitudMatricula($AsignaturaSolicitudMatricula);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($AsignaturaSolicitudMatricula>0){         updateAsignaturaSolicitudMatricula($AsignaturaSolicitudMatricula,$idSolicitudMatricula,$idAsignatura);      }else{         createAsignaturaSolicitudMatricula($idSolicitudMatricula,$idAsignatura);      }   }   header("location: http://".HOST."/sae");?>
