<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Asistencia =  $_REQUEST["Asistencia"];   $idMatriculaAsignatura =  $_REQUEST["idMatriculaAsignatura"];   $fecha =  $_REQUEST["fecha"];   $horas =  $_REQUEST["horas"];   if($boton == "BORRAR"){      deleteAsistencia($Asistencia);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Asistencia>0){         updateAsistencia($Asistencia,$idMatriculaAsignatura,$fecha,$horas);      }else{         createAsistencia($idMatriculaAsignatura,$fecha,$horas);      }   }   header("location: http://".HOST."/sae");?>
