<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Aula =  $_REQUEST["Aula"];   $capacidad =  $_REQUEST["capacidad"];   $descripcion =  $_REQUEST["descripcion"];   $idTipoAula =  $_REQUEST["idTipoAula"];   if($boton == "BORRAR"){      deleteAula($Aula);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Aula>0){         updateAula($Aula,$capacidad,$descripcion,$idTipoAula);      }else{         createAula($capacidad,$descripcion,$idTipoAula);      }   }   header("location: http://".HOST."/sae");?>
