<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $AulasAsignaturas =  $_REQUEST["AulasAsignaturas"];   $idAula =  $_REQUEST["idAula"];   $idDocenteAsignatura =  $_REQUEST["idDocenteAsignatura"];   if($boton == "BORRAR"){      deleteAulasAsignaturas($AulasAsignaturas);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($AulasAsignaturas>0){         updateAulasAsignaturas($AulasAsignaturas,$idAula,$idDocenteAsignatura);      }else{         createAulasAsignaturas($idAula,$idDocenteAsignatura);      }   }   header("location: http://".HOST."/sae");?>
