<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Carrera =  $_REQUEST["Carrera"];   $resolucion =  $_REQUEST["resolucion"];   $nombre =  $_REQUEST["nombre"];   $descripcion =  $_REQUEST["descripcion"];   $idModalidad =  $_REQUEST["idModalidad"];   if($boton == "BORRAR"){      deleteCarrera($Carrera);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Carrera>0){         updateCarrera($Carrera,$resolucion,$nombre,$descripcion,$idModalidad);      }else{         createCarrera($resolucion,$nombre,$descripcion,$idModalidad);      }   }   header("location: http://".HOST."/sae");?>
