<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Contacto =  $_REQUEST["Contacto"];   $idPersona =  $_REQUEST["idPersona"];   $descripcion =  $_REQUEST["descripcion"];   $contacto =  $_REQUEST["contacto"];   if($boton == "BORRAR"){      deleteContacto($Contacto);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Contacto>0){         updateContacto($Contacto,$idPersona,$descripcion,$contacto);      }else{         createContacto($idPersona,$descripcion,$contacto);      }   }   header("location: http://".HOST."/sae");?>
