<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $DatosEstudiante =  $_REQUEST["DatosEstudiante"];   $idEstudiante =  $_REQUEST["idEstudiante"];   $descripcion =  $_REQUEST["descripcion"];   $dato =  $_REQUEST["dato"];   if($boton == "BORRAR"){      deleteDatosEstudiante($DatosEstudiante);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($DatosEstudiante>0){         updateDatosEstudiante($DatosEstudiante,$idEstudiante,$descripcion,$dato);      }else{         createDatosEstudiante($idEstudiante,$descripcion,$dato);      }   }   header("location: http://".HOST."/sae");?>
