<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $DetalleNotas =  $_REQUEST["DetalleNotas"];   $descripcion =  $_REQUEST["descripcion"];   $nota =  $_REQUEST["nota"];   $idCateogiraNota =  $_REQUEST["idCateogiraNota"];   $idNota =  $_REQUEST["idNota"];   if($boton == "BORRAR"){      deleteDetalleNotas($DetalleNotas);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($DetalleNotas>0){         updateDetalleNotas($DetalleNotas,$descripcion,$nota,$idCateogiraNota,$idNota);      }else{         createDetalleNotas($descripcion,$nota,$idCateogiraNota,$idNota);      }   }   header("location: http://".HOST."/sae");?>
