<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Discapacidad =  $_REQUEST["Discapacidad"];   $idPersona =  $_REQUEST["idPersona"];   $idTipoDiscapacidad =  $_REQUEST["idTipoDiscapacidad"];   $porcentaje =  $_REQUEST["porcentaje"];   if($boton == "BORRAR"){      deleteDiscapacidad($Discapacidad);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Discapacidad>0){         updateDiscapacidad($Discapacidad,$idPersona,$idTipoDiscapacidad,$porcentaje);      }else{         createDiscapacidad($idPersona,$idTipoDiscapacidad,$porcentaje);      }   }   header("location: http://".HOST."/sae");?>
