<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Docente =  $_REQUEST["Docente"];   $idPersona =  $_REQUEST["idPersona"];   $fechaInicio =  $_REQUEST["fechaInicio"];   $idEstado =  $_REQUEST["idEstado"];   if($boton == "BORRAR"){      deleteDocente($Docente);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Docente>0){         updateDocente($Docente,$idPersona,$fechaInicio,$idEstado);      }else{         createDocente($idPersona,$fechaInicio,$idEstado);      }   }   header("location: http://".HOST."/sae");?>
