<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $DocenteAsignatura =  $_REQUEST["DocenteAsignatura"];   $idDocente =  $_REQUEST["idDocente"];   $idPeriodoLectivo =  $_REQUEST["idPeriodoLectivo"];   $idAsignatura =  $_REQUEST["idAsignatura"];   if($boton == "BORRAR"){      deleteDocenteAsignatura($DocenteAsignatura);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($DocenteAsignatura>0){         updateDocenteAsignatura($DocenteAsignatura,$idDocente,$idPeriodoLectivo,$idAsignatura);      }else{         createDocenteAsignatura($idDocente,$idPeriodoLectivo,$idAsignatura);      }   }   header("location: http://".HOST."/sae");?>
