<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Documento =  $_REQUEST["Documento"];   $documento =  $_REQUEST["documento"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteDocumento($Documento);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Documento>0){         updateDocumento($Documento,$documento,$descripcion);      }else{         createDocumento($documento,$descripcion);      }   }   header("location: http://".HOST."/sae");?>
