<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $EducacionContinua =  $_REQUEST["EducacionContinua"];   $descripcion =  $_REQUEST["descripcion"];   $horas =  $_REQUEST["horas"];   $fechaInicio =  $_REQUEST["fechaInicio"];   $fechaFin =  $_REQUEST["fechaFin"];   $idTipoEducacionContinua =  $_REQUEST["idTipoEducacionContinua"];   $lugar =  $_REQUEST["lugar"];   if($boton == "BORRAR"){      deleteEducacionContinua($EducacionContinua);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($EducacionContinua>0){         updateEducacionContinua($EducacionContinua,$descripcion,$horas,$fechaInicio,$fechaFin,$idTipoEducacionContinua,$lugar);      }else{         createEducacionContinua($descripcion,$horas,$fechaInicio,$fechaFin,$idTipoEducacionContinua,$lugar);      }   }   header("location: http://".HOST."/sae");?>
