<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Enfermedad =  $_REQUEST["Enfermedad"];   $descripcion =  $_REQUEST["descripcion"];   $observaciones =  $_REQUEST["observaciones"];   $tratamiento =  $_REQUEST["tratamiento"];   if($boton == "BORRAR"){      deleteEnfermedad($Enfermedad);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Enfermedad>0){         updateEnfermedad($Enfermedad,$descripcion,$observaciones,$tratamiento);      }else{         createEnfermedad($descripcion,$observaciones,$tratamiento);      }   }   header("location: http://".HOST."/sae");?>
