<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Estado =  $_REQUEST["Estado"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteEstado($Estado);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Estado>0){         updateEstado($Estado,$descripcion);      }else{         createEstado($descripcion);      }   }   header("location: http://".HOST."/sae");?>
