<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $EstadoCivil =  $_REQUEST["EstadoCivil"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteEstadoCivil($EstadoCivil);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($EstadoCivil>0){         updateEstadoCivil($EstadoCivil,$descripcion);      }else{         createEstadoCivil($descripcion);      }   }   header("location: http://".HOST."/sae");?>
