<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $EstadoSolicitud =  $_REQUEST["EstadoSolicitud"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteEstadoSolicitud($EstadoSolicitud);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($EstadoSolicitud>0){         updateEstadoSolicitud($EstadoSolicitud,$descripcion);      }else{         createEstadoSolicitud($descripcion);      }   }   header("location: http://".HOST."/sae");?>
