<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Estudiante =  $_REQUEST["Estudiante"];   $idColegio =  $_REQUEST["idColegio"];   if($boton == "BORRAR"){      deleteEstudiante($Estudiante);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Estudiante>0){         updateEstudiante($Estudiante,$idColegio);      }else{         createEstudiante($idColegio);      }   }   header("location: http://".HOST."/sae");?>
