<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $ExperienciaLaboral =  $_REQUEST["ExperienciaLaboral"];   $idPersona =  $_REQUEST["idPersona"];   $fechaInicio =  $_REQUEST["fechaInicio"];   $fechaFin =  $_REQUEST["fechaFin"];   $descripcionCargo =  $_REQUEST["descripcionCargo"];   $descripcionFunciones =  $_REQUEST["descripcionFunciones"];   $nombreEmpresa =  $_REQUEST["nombreEmpresa"];   $idMotivoSalida =  $_REQUEST["idMotivoSalida"];   if($boton == "BORRAR"){      deleteExperienciaLaboral($ExperienciaLaboral);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($ExperienciaLaboral>0){         updateExperienciaLaboral($ExperienciaLaboral,$idPersona,$fechaInicio,$fechaFin,$descripcionCargo,$descripcionFunciones,$nombreEmpresa,$idMotivoSalida);      }else{         createExperienciaLaboral($idPersona,$fechaInicio,$fechaFin,$descripcionCargo,$descripcionFunciones,$nombreEmpresa,$idMotivoSalida);      }   }   header("location: http://".HOST."/sae");?>
