<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Genero =  $_REQUEST["Genero"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteGenero($Genero);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Genero>0){         updateGenero($Genero,$descripcion);      }else{         createGenero($descripcion);      }   }   header("location: http://".HOST."/sae");?>
