<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Hobbies =  $_REQUEST["Hobbies"];   $idPersona =  $_REQUEST["idPersona"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteHobbies($Hobbies);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Hobbies>0){         updateHobbies($Hobbies,$idPersona,$descripcion);      }else{         createHobbies($idPersona,$descripcion);      }   }   header("location: http://".HOST."/sae");?>
