<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Institucion =  $_REQUEST["Institucion"];   $nombre =  $_REQUEST["nombre"];   $idUbicacion =  $_REQUEST["idUbicacion"];   $tipo =  $_REQUEST["tipo"];   if($boton == "BORRAR"){      deleteInstitucion($Institucion);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Institucion>0){         updateInstitucion($Institucion,$nombre,$idUbicacion,$tipo);      }else{         createInstitucion($nombre,$idUbicacion,$tipo);      }   }   header("location: http://".HOST."/sae");?>
