<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Jornada =  $_REQUEST["Jornada"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteJornada($Jornada);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Jornada>0){         updateJornada($Jornada,$descripcion);      }else{         createJornada($descripcion);      }   }   header("location: http://".HOST."/sae");?>
