<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Malla =  $_REQUEST["Malla"];   $fechaMallaInicio =  $_REQUEST["fechaMallaInicio"];   $fechaMallaFin =  $_REQUEST["fechaMallaFin"];   $idCarrera =  $_REQUEST["idCarrera"];   $idDocResolucion =  $_REQUEST["idDocResolucion"];   if($boton == "BORRAR"){      deleteMalla($Malla);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Malla>0){         updateMalla($Malla,$fechaMallaInicio,$fechaMallaFin,$idCarrera,$idDocResolucion);      }else{         createMalla($fechaMallaInicio,$fechaMallaFin,$idCarrera,$idDocResolucion);      }   }   header("location: http://".HOST."/sae");?>
