<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Matricula =  $_REQUEST["Matricula"];   $codigo =  $_REQUEST["codigo"];   $idEstudiante =  $_REQUEST["idEstudiante"];   $idPeriodoLectivo =  $_REQUEST["idPeriodoLectivo"];   $idJornada =  $_REQUEST["idJornada"];   $idMalla =  $_REQUEST["idMalla"];   $idTipoMatricula =  $_REQUEST["idTipoMatricula"];   if($boton == "BORRAR"){      deleteMatricula($Matricula);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Matricula>0){         updateMatricula($Matricula,$codigo,$idEstudiante,$idPeriodoLectivo,$idJornada,$idMalla,$idTipoMatricula);      }else{         createMatricula($codigo,$idEstudiante,$idPeriodoLectivo,$idJornada,$idMalla,$idTipoMatricula);      }   }   header("location: http://".HOST."/sae");?>
