<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $MatriculaAsignatura =  $_REQUEST["MatriculaAsignatura"];   $idMatricula =  $_REQUEST["idMatricula"];   $idAsignatura =  $_REQUEST["idAsignatura"];   $numeroMatricula =  $_REQUEST["numeroMatricula"];   $idParalelo =  $_REQUEST["idParalelo"];   if($boton == "BORRAR"){      deleteMatriculaAsignatura($MatriculaAsignatura);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($MatriculaAsignatura>0){         updateMatriculaAsignatura($MatriculaAsignatura,$idMatricula,$idAsignatura,$numeroMatricula,$idParalelo);      }else{         createMatriculaAsignatura($idMatricula,$idAsignatura,$numeroMatricula,$idParalelo);      }   }   header("location: http://".HOST."/sae");?>
