<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $NivelTitulo =  $_REQUEST["NivelTitulo"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteNivelTitulo($NivelTitulo);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($NivelTitulo>0){         updateNivelTitulo($NivelTitulo,$descripcion);      }else{         createNivelTitulo($descripcion);      }   }   header("location: http://".HOST."/sae");?>
