<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Notas =  $_REQUEST["Notas"];   $idParcial =  $_REQUEST["idParcial"];   $idMatriculaAsignatura =  $_REQUEST["idMatriculaAsignatura"];   if($boton == "BORRAR"){      deleteNotas($Notas);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Notas>0){         updateNotas($Notas,$idParcial,$idMatriculaAsignatura);      }else{         createNotas($idParcial,$idMatriculaAsignatura);      }   }   header("location: http://".HOST."/sae");?>
