<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Paralelo =  $_REQUEST["Paralelo"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteParalelo($Paralelo);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Paralelo>0){         updateParalelo($Paralelo,$descripcion);      }else{         createParalelo($descripcion);      }   }   header("location: http://".HOST."/sae");?>
