<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Parcial =  $_REQUEST["Parcial"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteParcial($Parcial);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Parcial>0){         updateParcial($Parcial,$descripcion);      }else{         createParcial($descripcion);      }   }   header("location: http://".HOST."/sae");?>
