<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $PeriodoAcademico =  $_REQUEST["PeriodoAcademico"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deletePeriodoAcademico($PeriodoAcademico);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($PeriodoAcademico>0){         updatePeriodoAcademico($PeriodoAcademico,$descripcion);      }else{         createPeriodoAcademico($descripcion);      }   }   header("location: http://".HOST."/sae");?>
