<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $PeriodoLectivo =  $_REQUEST["PeriodoLectivo"];   $descripcion =  $_REQUEST["descripcion"];   $fechaInicio =  $_REQUEST["fechaInicio"];   $fechaFin =  $_REQUEST["fechaFin"];   if($boton == "BORRAR"){      deletePeriodoLectivo($PeriodoLectivo);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($PeriodoLectivo>0){         updatePeriodoLectivo($PeriodoLectivo,$descripcion,$fechaInicio,$fechaFin);      }else{         createPeriodoLectivo($descripcion,$fechaInicio,$fechaFin);      }   }   header("location: http://".HOST."/sae");?>
