<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Persona =  $_REQUEST["Persona"];   $identificacion =  $_REQUEST["identificacion"];   $nombre1 =  $_REQUEST["nombre1"];   $nombre2 =  $_REQUEST["nombre2"];   $apellido1 =  $_REQUEST["apellido1"];   $apellido2 =  $_REQUEST["apellido2"];   $fechaNacimiento =  $_REQUEST["fechaNacimiento"];   $idGenero =  $_REQUEST["idGenero"];   $idDomicilio =  $_REQUEST["idDomicilio"];   $idEstadoCivil =  $_REQUEST["idEstadoCivil"];   $idUbicacionNacimiento =  $_REQUEST["idUbicacionNacimiento"];   $ingresos =  $_REQUEST["ingresos"];   $idTipoSangre =  $_REQUEST["idTipoSangre"];   $numeroHijos =  $_REQUEST["numeroHijos"];   $idOcupacion =  $_REQUEST["idOcupacion"];   $foto =  $_REQUEST["foto"];   if($boton == "BORRAR"){      deletePersona($Persona);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Persona>0){         updatePersona($Persona,$identificacion,$nombre1,$nombre2,$apellido1,$apellido2,$fechaNacimiento,$idGenero,$idDomicilio,$idEstadoCivil,$idUbicacionNacimiento,$ingresos,$idTipoSangre,$numeroHijos,$idOcupacion,$foto);      }else{         createPersona($identificacion,$nombre1,$nombre2,$apellido1,$apellido2,$fechaNacimiento,$idGenero,$idDomicilio,$idEstadoCivil,$idUbicacionNacimiento,$ingresos,$idTipoSangre,$numeroHijos,$idOcupacion,$foto);      }   }   header("location: http://".HOST."/sae");?>
