<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Ponderacion =  $_REQUEST["Ponderacion"];   $idCategoria =  $_REQUEST["idCategoria"];   $idParcial =  $_REQUEST["idParcial"];   $porcentaje =  $_REQUEST["porcentaje"];   if($boton == "BORRAR"){      deletePonderacion($Ponderacion);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Ponderacion>0){         updatePonderacion($Ponderacion,$idCategoria,$idParcial,$porcentaje);      }else{         createPonderacion($idCategoria,$idParcial,$porcentaje);      }   }   header("location: http://".HOST."/sae");?>
