<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Requisito =  $_REQUEST["Requisito"];   $idAsignaturaDependiente =  $_REQUEST["idAsignaturaDependiente"];   $idAsignaturaIndependiente =  $_REQUEST["idAsignaturaIndependiente"];   $idTipoRequisito =  $_REQUEST["idTipoRequisito"];   if($boton == "BORRAR"){      deleteRequisito($Requisito);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Requisito>0){         updateRequisito($Requisito,$idAsignaturaDependiente,$idAsignaturaIndependiente,$idTipoRequisito);      }else{         createRequisito($idAsignaturaDependiente,$idAsignaturaIndependiente,$idTipoRequisito);      }   }   header("location: http://".HOST."/sae");?>
