<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $SolicitudMatricula =  $_REQUEST["SolicitudMatricula"];   $codigo =  $_REQUEST["codigo"];   $fecha =  $_REQUEST["fecha"];   $idPeriodoAcademico =  $_REQUEST["idPeriodoAcademico"];   $idPeriodoLectivo =  $_REQUEST["idPeriodoLectivo"];   $idEstadoSolicitud =  $_REQUEST["idEstadoSolicitud"];   if($boton == "BORRAR"){      deleteSolicitudMatricula($SolicitudMatricula);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($SolicitudMatricula>0){         updateSolicitudMatricula($SolicitudMatricula,$codigo,$fecha,$idPeriodoAcademico,$idPeriodoLectivo,$idEstadoSolicitud);      }else{         createSolicitudMatricula($codigo,$fecha,$idPeriodoAcademico,$idPeriodoLectivo,$idEstadoSolicitud);      }   }   header("location: http://".HOST."/sae");?>
