<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $TipoDiscapacidad =  $_REQUEST["TipoDiscapacidad"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteTipoDiscapacidad($TipoDiscapacidad);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($TipoDiscapacidad>0){         updateTipoDiscapacidad($TipoDiscapacidad,$descripcion);      }else{         createTipoDiscapacidad($descripcion);      }   }   header("location: http://".HOST."/sae");?>
