<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $TipoEducacionContinua =  $_REQUEST["TipoEducacionContinua"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteTipoEducacionContinua($TipoEducacionContinua);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($TipoEducacionContinua>0){         updateTipoEducacionContinua($TipoEducacionContinua,$descripcion);      }else{         createTipoEducacionContinua($descripcion);      }   }   header("location: http://".HOST."/sae");?>
