<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $TipoRequisito =  $_REQUEST["TipoRequisito"];   $descripcion =  $_REQUEST["descripcion"];   if($boton == "BORRAR"){      deleteTipoRequisito($TipoRequisito);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($TipoRequisito>0){         updateTipoRequisito($TipoRequisito,$descripcion);      }else{         createTipoRequisito($descripcion);      }   }   header("location: http://".HOST."/sae");?>
