<?php   require_once './../../../libs/saewsclient/known/functions.php';   require_once './../../../libs/saewsclient/const/defaults.php';   $boton =  $_REQUEST["submit"];   $Titulo =  $_REQUEST["Titulo"];   $idPersona =  $_REQUEST["idPersona"];   $idInstitucion =  $_REQUEST["idInstitucion"];   $codigoRegistro =  $_REQUEST["codigoRegistro"];   $idNivelTitulo =  $_REQUEST["idNivelTitulo"];   if($boton == "BORRAR"){      deleteTitulo($Titulo);   }   if($boton == "GUARDAR" || $boton == "ACTUALIZAR"){      if($Titulo>0){         updateTitulo($Titulo,$idPersona,$idInstitucion,$codigoRegistro,$idNivelTitulo);      }else{         createTitulo($idPersona,$idInstitucion,$codigoRegistro,$idNivelTitulo);      }   }   header("location: http://".HOST."/sae");?>
