<?php   require_once './../../../libs/saewsclient/known/functions.php';   $asignatura =  $_REQUEST["asignatura"];   if($asignatura==""){      $respuesta = json_decode(readAsignaturaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readAsignatura($asignatura));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
