<?php   require_once './../../../libs/saewsclient/known/functions.php';   $asignaturasolicitudmatricula =  $_REQUEST["asignaturasolicitudmatricula"];   if($asignaturasolicitudmatricula==""){      $respuesta = json_decode(readAsignaturaSolicitudMatriculaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readAsignaturaSolicitudMatricula($asignaturasolicitudmatricula));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
