<?php   require_once './../../../libs/saewsclient/known/functions.php';   $asistencia =  $_REQUEST["asistencia"];   if($asistencia==""){      $respuesta = json_decode(readAsistenciaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readAsistencia($asistencia));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
