<?php   require_once './../../../libs/saewsclient/known/functions.php';   $aula =  $_REQUEST["aula"];   if($aula==""){      $respuesta = json_decode(readAulaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readAula($aula));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
