<?php   require_once './../../../libs/saewsclient/known/functions.php';   $aulasasignaturas =  $_REQUEST["aulasasignaturas"];   if($aulasasignaturas==""){      $respuesta = json_decode(readAulasAsignaturasAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readAulasAsignaturas($aulasasignaturas));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
