<?php   require_once './../../../libs/saewsclient/known/functions.php';   $carrera =  $_REQUEST["carrera"];   if($carrera==""){      $respuesta = json_decode(readCarreraAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readCarrera($carrera));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
