<?php   require_once './../../../libs/saewsclient/known/functions.php';   $contacto =  $_REQUEST["contacto"];   if($contacto==""){      $respuesta = json_decode(readContactoAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readContacto($contacto));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
