<?php   require_once './../../../libs/saewsclient/known/functions.php';   $datosestudiante =  $_REQUEST["datosestudiante"];   if($datosestudiante==""){      $respuesta = json_decode(readDatosEstudianteAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readDatosEstudiante($datosestudiante));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
