<?php   require_once './../../../libs/saewsclient/known/functions.php';   $detallenotas =  $_REQUEST["detallenotas"];   if($detallenotas==""){      $respuesta = json_decode(readDetalleNotasAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readDetalleNotas($detallenotas));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
